    print substr(seq_ext,16+frame,3), substr(seq_ext,31+frame,3), substr(seq_ext,34+frame,3), substr(seq_ext,37+frame,3) # A,+5,+6 and +7 sites","\t    }","     }","","}' $1 $2 | awk 'BEGIN{FS=OFS=\"\\t\"}{","","\t for(i=1;i<=4;i++){","","\t \tif($i!~/N/ && $i!=\"TGA\" && $i!=\"TAG\" && $i!=\"TAA\"){","\t \t\tsum[i]++","            Codon[i\"\\t\"$i]++","        } ","     }","","}END{","    for(j in Codon){","        ","        split(j,ja,\"\\t\")","        ","        # Normalized to the mean of +5, +6 and +7 sites","        if(ja[1]==1){ ","    \t   ","           fA=Codon[j]/sum[1]","           f1=Codon[2\"\\t\"ja[2]]/sum[2]","           f2=Codon[3\"\\t\"ja[2]]/sum[3]","           f3=Codon[4\"\\t\"ja[2]]/sum[4]","           print ja[2],fA*3/(f1+f2+f3)","","        }","    }","","}' > Codon_occupancy.txt"],"stylingDirectives":[[{"start":0,"end":11,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[{"start":0,"end":13,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[],[],[{"start":0,"end":122,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[],[{"start":8,"end":10,"cssClass":"pl-smi"}],[{"start":8,"end":10,"cssClass":"pl-smi"}],[],[],[{"start":4,"end":32,"cssClass":"pl-s"},{"start":4,"end":5,"cssClass":"pl-pds"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":16,"cssClass":"pl-s"}],[{"start":0,"end":26,"cssClass":"pl-s"}],[{"start":0,"end":29,"cssClass":"pl-s"}],[{"start":0,"end":11,"cssClass":"pl-s"}],[{"start":0,"end":45,"cssClass":"pl-s"}],[{"start":0,"end":5,"cssClass":"pl-s"}],[{"start":0,"end":31,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":6,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":33,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":24,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":65,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":28,"cssClass":"pl-s"}],[{"start":0,